import React, { Component } from 'react';
import './Calculator.css';
import Button from '../components/Button';
import Display from '../components/Display';

const initalState = {
    displayValue: '0',
    clearDisplay: false,
    opeation: null,
    values: [0, 0],
}

export default class Calculator extends Component {

    constructor(props){
        super(props)

        this.clearMemory = this.clearMemory.bind(this);
        this.setOperation = this.setOperation.bind(this);
        this.addDigit = this.addDigit.bind(this);
    }

    clearMemory(){
        console.log('limpar')
    }

    setOperation(operation){
        console.log('operation')
    }

    addDigit(n){
        console.log(n)
    }

    render () {

        return (
            <div className="calculator">
                <Display value={0}></Display>
                <Button label="AC" click={this.clearMemory} triple></Button>
                <Button label="/" click={this.setOperation} operation></Button>
                <Button label="7" clicl={this.addDigit}></Button>
                <Button label="8" clicl={this.addDigit}></Button>
                <Button label="9" clicl={this.addDigit}></Button>
                <Button label="*" clicl={this.setOperation} operation></Button>
                <Button label="4" clicl={this.addDigit}></Button>
                <Button label="5" clicl={this.addDigit}></Button>
                <Button label="6" clicl={this.addDigit}></Button>
                <Button label="-" clicl={this.setOperation}operation></Button>
                <Button label="1" clicl={this.addDigit}></Button>
                <Button label="2" clicl={this.addDigit}></Button>
                <Button label="3" clicl={this.addDigit}></Button>
                <Button label="+" clicl={this.setOperation} operation></Button>
                <Button label="0" clicl={this.addDigit} double></Button>
                <Button label="." clicl={this.addDigit}></Button>
                <Button label="=" clicl={this.setOperation} operation></Button>
                </div>
        )
    }
}
